sql.if:
and exists (select null from publication_contributor join contributor on publication_contributor.contributor_id = contributor.contributor_id where publication.publication_id = publication_contributor.publication_id and
contributor.orcid = #{oid}
)

and exists (select null from publication_cost_center join affiliation on publication_cost_center.cost_center_id = affiliation.affiliation_id where publication.publication_id = publication_cost_center.publication_id and
upper(name) like '%' || upper(#{co}) || '%'
)

and exists (select null from publication_link join link_type on publication_link.link_type_id = link_type.link_type_id where publication.publication_id = publication_link.publication_id and
upper(link_type.name) = upper(#{lt})
)

and not exists (select null from publication_link join link_type on publication_link.link_type_id = link_type.link_type_id where publication.publication_id = publication_link.publication_id and
upper(link_type.name) = upper(#{lt})
)

mapper.sql:
(display_to_public_date is null or
 display_to_public_date <= timezone('US/Eastern'::text, now())
 )
